    func doTest(_ mdKind: MessageDigest.Kind, _ tests: [testStruct]) {
        let md = MessageDigest(mdKind)
        for t in tests {
            md.update(t.seed)
            XCTAssertEqual(md.digest(), t.output)
        }
    }

